import React from 'react';
import SearchBar from './SearchBar';
import MovieCard from './MovieCard';
import AddMovie from './AddMovie';
import MovieList from './MovieList';

class MovieLibrary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchText: '',
      bookmarkedOnly: false,
      selectedGenre: '',
      movies: this.props.movies,
    };
    this.changingStatus = this.changingStatus.bind(this);
  }

  changingStatus(state) {
    this.setState({
      searchText: state.target.value });
  }

  render() {
    // const { movie } = this.props;
    return (
      <div >
        <SearchBar
          searchText={this.state.searchText}
          onSearchTextChange={this.changingStatus}
          bookmarkedOnly={this.state.bookmarkedOnly}
          onBookmarkedChange={this.bookmarkedOnly}
          selectedGenre={this.state.selectedGenre}
          onSelectedGenreChange={this.selectedGenre}
        />
        <AddMovie />
        {/* <MovieCard /> */}
        {/* <MovieList /> */}
      </div>
    );
  }
}


export default MovieLibrary;
