import React from 'react';
import '../styles/pages/SeachBar.css';
import { Form } from 'react-bootstrap';

class SearchBar extends React.Component {// barra de pesquisa
  render() {
    const {
      searchText,
      onSearchTextChange,
      bookmarkedOnly,
      onBookmarkedChange,
      selectedGenre,
      onSelectedGenreChange,
    } = this.props;
    return (
      <div className="seach-bar">
        <div className="content-wrapper">
          <form>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Pesquisa por Título </Form.Label>
              <Form.Control type="text" id="include" value={searchText} onChange={onSearchTextChange} />
            </Form.Group>
            {/* <section>
              <label htmlFor="include">Pesquisa por Título </label>
              <input type="text" id="include" value={searchText} onChange={onSearchTextChange} />
            </section> */}

            <Form.Group controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="Mostrar Favoritos" checked={bookmarkedOnly} onChange={onBookmarkedChange} />
            </Form.Group>
            <div className="mb-2">
    {[DropdownButton, SplitButton].map((DropdownType, idx) => (
      <DropdownType
        as={ButtonGroup}
        key={idx}
        id={`dropdown-button-drop-${idx}`}
        size="lg"
        title="Drop large"
      >
        <Dropdown.Item eventKey="1">Action</Dropdown.Item>
        <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
        <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
      </DropdownType>
    ))}
  </div>
            <section>
              <label htmlFor="filterGender">Filtrar por gênero</label>
              <select value={selectedGenre} id="filterGender" onChange={onSelectedGenreChange}>
                <option value="">Todos</option>
                <option value="action">Ação</option>
                <option value="comedy">Comédia</option>
                <option value="thriller">Suspense</option>
              </select>
            </section>
          </form>
        </div>
      </div>
    );
  }
}

export default SearchBar;
