import { REQUISICAO_BEM_SUCEDIDA, REQUISICAO_API,  } from '../actions/action';

const STATE_INICIAL = {
  loading: false,
};

const VerificaActions = (state = STATE_INICIAL, action) => {
  if (action.type === REQUISICAO_BEM_SUCEDIDA) {
    return {
      ...state,
      loadind: false,
    };
  }

  if (action.type === REQUISICAO_API) {
    return {
      ...state,
      loading: false,
      payload: action.error,
    };
  }
  return state;
};

export default VerificaActions;

