import StarWarsPlanetsAPI  from '../../services/StarWarsPlanetsAPI';

export const REQUISICAO_API = 'REQUISICAO_BEM_SUCEDIDA';
export const REQUISICAO_MAL_SUCEDIDA = 'REQUISICAO_MAL_SUCEDIDA';
export const LOADING = 'LOADING';

export const requicaoAPI = (payload) => ({ /* Requisição API */
  type: REQUISICAO_API,
  loading: true,
});

export const requicaoBemSucedida = () => ({ /* requisição bem sucedida */
  type: REQUISICAO_BEM_SUCEDIDA,
  loading: false,
});

function ReduxThunk() {
  return (dispatch) => {
    dispatch(loading());
    return StarWarsPlanetsAPI()
      .then(
        (batata) => dispatch(requicaoAPI(batata)),
      );
  };
}

export default ReduxThunk;

