import React from 'react';
import { connect } from 'react-redux';
import { filterValues } from '../actions/actionFilterPlanetsName';
class FiltroNumericos extends React.Component {
  constructor(props) {
    super(props);
    console.log('props', this.props);
    this.state = {
      column: '',
      comparison: '',
      value: 0,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    event.preventDefault();
    const { onClick } = this.props;
    onClick(this.state);
    this.setState({
      column: '',
      comparison: '',
      value: 0,
    });
  }

  setFilterValue(event) {
    const { name, value } = event.target;
    this.setState({ [name]: name === 'value' ? parseFloat(value) : value });
  }

  render() {
    const setFilterValue = this.setFilterValue;
    const { data, dispatchFilterValues, comparison, column, value } = this.props;
    console.log('FiltroNumericos', this.props);
    return (
      <div>
        <select data-testid="column-filter" name={column} value={column} onChange={setFilterValue}>
          <option disabled selected>Coluna</option>
          <option>population</option>
          <option>orbital_period</option>
          <option>diameter</option>
          <option>rotation_period</option>
          <option>surface_water</option>
        </select>
        <select data-testid="comparison-filter" name={comparison} value={comparison} onChange={setFilterValue}>
          <option disabled selected>Compara√ß√£o</option>
          <option>Maior que</option>
          <option>Menor que</option>
          <option>Igual</option>
        </select>
        <input type="number" data-testid="value-filter" name={value} value={value} onChange={setFilterValue} />
        <button data-testid="button-filter" onClick={() => dispatchFilterValues(this.handleChange)}>Filtrar</button>
      </div>
    );
  }
}
render()
const mapStateToProps = (state) => {
  console.log('filterByNumericValues', state);
  return {
    column: state.reducerFilter.column,
    comparison: state.reducerFilter.comparison,
    value: state.reducerFilter.value,
    data: state.planetsReducer.data,
  };
};

const mapDispatchToProps = (dispatch) => ({
  dispatchFilterValues: (column, comparison, value) => dispatch(filterValues(column, comparison, value)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FiltroNumericos);
