import StarWarsPlanetsAPI from '../../services/StarWarsPlanetsAPI';

export const REQUISICAO_BEM_SUCEDIDA = 'REQUISICAO_BEM_SUCEDIDA';
export const RecebendoREQUISICAO_API = 'REQUISICAO_API';

export const recebendoRequicaoAPI = () => ({ /* Requisição API */
  type: REQUISICAO_API,
  carregando: true,
});

export const requicaoBemSucedida = (planets) => ({ /* requisição bem sucedida */
  type: REQUISICAO_BEM_SUCEDIDA,
  resultPlanets: planets.results, /* Puxando o results da StarWarsPlanetsAPI.js */
});

function ReduxThunk() {
  return (dispatch) => {
    dispatch(recebendoRequicaoAPI());
    return StarWarsPlanetsAPI()
      .then(
        (e) => dispatch(requicaoBemSucedida(e)),
      );
  };
}

export default ReduxThunk;

