const STATE_INICIAL = {
  filterByName: {
    name: '',
  },
  filterByNumericValues: [{ column, comparison, value }],
};

const filters = (state = STATE_INICIAL, action) => {
  // console.log('action reducer', action);
  switch (action.type) {
    case FILTER_NAME_PLANET:
      return {
        ...state,
        filterByName: {
          name: action.name,
        },
      };
