import React from 'react';
import { connect } from 'react-redux';
import { filterValues } from '../actions/actionFilterPlanetsName';

const renderFiltroNumerico = (data, column) => {
  console.log(column)
  let coluna = data;
  if(column !== '') {
    coluna.filter((element) => element.column.includes(column));
  }
}

class FiltroNumericos extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      column: '',
      comparison: '',
      value: 0,
    }
    this.handleColumnChange = this.handleColumnChange.bind(this);
    this.handleComparisonChange = this.handleComparisonChange.bind(this);
    this.handleValueChange = this.handleValueChange.bind(this);
  }



  handleColumnChange(event) {
    this.setState({ 
      column: event.target.value 
    });
  }

  handleComparisonChange(event) {
    this.setState({ comparison: event.target.value });
  }

  handleValueChange(event) {
    this.setState({ value: event.target.value });
  }

  render() {
    const { data, dispatchFilterValues } = this.props;
    console.log('FiltroNumericos', this.props);
    return (
      <div>
        <select data-testid="column-filter" onChange={this.handleColumnChange}>
          <option disabled selected>Coluna</option>
          <option>population</option>
          <option>orbital_period</option>
          <option>diameter</option>
          <option>rotation_period</option>
          <option>surface_water</option>
        </select>
        <select data-testid="comparison-filter" onChange={this.handleComparisonChange}>
          <option disabled selected>Comparação</option>
          <option>Maior que</option>
          <option>Menor que</option>
          <option>Igual</option>
          {renderFiltroNumerico(data)}
        </select>
        <input type="number" data-testid="value-filter" onChange={this.handleValueChange} />
        <button data-testid="button-filter"onClick={() => dispatchFilterValues(this.state.column, this.state.comparison, this.state.value)}>Filtrar</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('filterByNumericValues', state.column);
  renderFiltroNumerico(state)
  return {
    column: state.reducerFilter.column,
    comparison: state.reducerFilter.comparison,
    value: state.reducerFilter.value,
    data: state.planetsReducer.data,
  };
};

const mapDispatchToProps = (dispatch) => ({
  dispatchFilterValues: (column, comparison, value) => dispatch(filterValues(column, comparison, value)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FiltroNumericos);
