import { REQUISICAO_BEM_SUCEDIDA, FAZENDO_REQUISICAO_API } from '../actions/action';

const STATE_INICIAL = {
  fazendoRequisicao: true,
  resultPlanets: []
};

const VerificaActions = (state = STATE_INICIAL, action) => {
  console.log(action);
  if (action.type === FAZENDO_REQUISICAO_API) {
    return {
      ...state,
      fazendoRequisicao: action.fazendoRequisicao,
    };
  }

  if (action.type === REQUISICAO_BEM_SUCEDIDA) {
    return {
      ...state, 
      resultPlanets: action.results,
      fazendoRequisicao: false,
    };
  }
  return state;
};

export default VerificaActions;

