import React from 'react';
import { connect } from 'react-redux';
import TableHeaders from './TableHeaders';
import { requicaoBemSucedida } from '../redux/actions/action';

class Table extends React.Component {
  render() {
    const { loading, payload, error } = this.props;
    if (!loading && payload) {
      return (
        <div>
          <tr>
            <TableHeaders />
            <th>name</th>
            <th>rotation_period</th>
            <th>orbital_period</th>
            <th>diameter</th>
            <th>climate</th>
            <th>gravity</th>
            <th>terrain</th>
            <th>surface_water</th>
            <th>population</th>
          </tr>
          <tr>
            <td onChange={() => requicaoBemSucedida()}>{payload.name}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.rotation_period}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.diameter}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.climate}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.gravity}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.terrain}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.surface_water}</td>
            <td onChange={() => requicaoBemSucedida()}>{payload.population}</td>
          </tr>
        </div>
      );
    }
  }
}

const mapStateToProps = (state) => ({
  payload: state.payload
});

const mapDispatchToProps = (dispatch) => ({
  requicaoBemSucedida: e => dispatch(changeSignal(e)),
});
export default connect(mapStateToProps, mapDispatchToProps)(Table);
