import React from 'react';

class FilterText extends React.Component {
  render() {
    return (
      <div>
        <label htmlFor="searchForPlanet">Procurar: </label>
        <input data-testid='name-filter' id="searchForPlanet">{}</input>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filtrando: state.planetsReducer.filters,
  }
}

export default FilterText;
