import { FILTER_NAME_PLANET, FILTER_VALUES, SET_VALUE_OPTIONS } from '../actions/actionFilterPlanetsName';

const STATE_INICIAL = {
  filterByName: {
    name: '',
  },
  filterByNumericValues: [{ column: 'surface_water', comparison: 'menor que', value: '40' },
  { column: 'diameter', comparison: 'maior que', value: '8900' },
  { column: 'population', comparison: 'igual a', value: '200000' },],
};

const filters = (state = STATE_INICIAL, action) => {
  console.log('action reducer', action);
  switch (action.type) {
    case FILTER_NAME_PLANET:
      return {
        ...state,
        filterByName: {
          name: action.value,
        },
      };
    case FILTER_VALUES:
      return {
        ...state,
        filterByNumericValues: [
          ...state.filterByNumericValues,
          { column: action.column, comparison: action.comparison, value: action.value },
        ],
      };
    default:
      return state;
  }
};

export default ReducerFilter;
