import React from 'react';
import { connect } from 'react-redux';

class TableHeaders extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      payload: [],
      error,
    };
  }

  handleChange(event) {
    const { id, valeu } = event.target;
    this.setState({
      [id]: valeu,
    })
  }

  render() {
    return (
      <form>
        <label htmlFor="filtro" >Procurar: </label>
        <input id="filtro" type="text"/>
      </form>
    )
  }
};

export default TableHeaders;
