import StarWarsPlanetsAPI  from '../../services/StarWarsPlanetsAPI';

export const REQUISICAO_BEM_SUCEDIDA = 'REQUISICAO_BEM_SUCEDIDA';
export const REQUISICAO_API = 'REQUISICAO_API';

export const requicaoAPI = () => ({ /* Requisição API */
  type: REQUISICAO_API,
  loading: true,
});

export const requicaoBemSucedida = (planets) => ({ /* requisição bem sucedida */
  type: REQUISICAO_BEM_SUCEDIDA,
  loading: planets,
});

function ReduxThunk() {
  return (dispatch) => {
    dispatch(requicaoAPI());
    return StarWarsPlanetsAPI()
      .then(
        (batata) => dispatch(requicaoAPI(batata)),
      );
  };
}

export default ReduxThunk;

