import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import fetchAPIStarWarsPlanets from '../actions/action';
import filterPlanetsName from '../actions/filterPlanetsName';
import Headers from './Headers';

class Table extends React.Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    const { StarWarsPlanetsAPI } = this.props;
    console.log('api', StarWarsPlanetsAPI);
    StarWarsPlanetsAPI();
    /*
    componentDidMount,quando montado, toda vez que o
    componente é renderizado é feita umaChamada na API.
    */
  }

  filtrarNomesDePlanetas = (filtrando, resultPlanets) => {
    const filtro = resultPlanets.filter((planeta) => planeta.name.includes(filtrando.name));
    return filtro;
  }

  todosOsPlanetas = (resultPlanets) => {
    const todo = resultPlanets.filter((planeta) => console.log('sgsgs', planeta.name))
    console.log(todo)
    return todo;
  }
  render() {
    const { resultPlanets, fazendoRequisicao, filtrando, filterPlanetsName } = this.props;
    // console.log('filterPlanetsName', filterPlanetsName)
    // console.log('resultPlanets', resultPlanets);
    // console.log('fazendoRequisicao', fazendoRequisicao);
    console.log('filtrando', filtrando);
    return (
      <div>
        <label htmlFor="searchForPlanet">Procurar: </label>
        <input data-testid='name-filter' id="searchForPlanet" onChange={(event) => filterPlanetsName(event.target.value)} />
        <table>
          <Headers />
          <tbody>
            {resultPlanets.map((planet) => (
              <tr>
                <td>{planet.name}</td>
                <td>{planet.rotation_period}</td>
                <td>{planet.diameter}</td>
                <td>{planet.climate}</td>
                <td>{planet.gravity}</td>
                <td>{planet.terrain}</td>
                <td>{planet.surface_water}</td>
              </tr>
            ))
            }
          </tbody>
        </table>
        {fazendoRequisicao && 'Loading...'}
      </div>
    );
  }
}

/*
  {fazendoRequisicao && 'Loading...'} enquanto fazendoRequisicao
  for true, o texto loading vai aparecer na tela.
*/

/*
  mapStateToProps faz o papel do subscribe no redux
  e no react faz papel no render
*/

/* os states que vou usar mapStateToProps vem do reducer initial_state*/
/*
  O valor do state do verificandoRequisicaoAPI
  vai ser três infos(o state, o reducer que
  contêm a action e a action que quero)
*/
const mapStateToProps = (state) => {
  console.log('state', state);
  return {
    fazendoRequisicao: state.planetsReducer.fazendoRequisicao,
    resultPlanets: state.planetsReducer.resultPlanets,
    filtrando: state.reducerFilter.filterByName,
  };
};

const mapDispatchToProps = (dispatch) => {
  console.log('verificando dispatch', dispatch);
  return {
    StarWarsPlanetsAPI: () => dispatch(fetchAPIStarWarsPlanets()),
    filterPanetsName: (event) => dispatch(filterPlanetsName(event)),
  };
};

Table.propTypes = {
  resultPlanets: PropTypes.arrayOf.isRequired,
  StarWarsPlanetsAPI: PropTypes.func.isRequired,
  fazendoRequisicao: PropTypes.bool.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Table);
