import React from 'react';
import { connect } from 'react-redux';
import { ReduxThunk } from '../';

class TableHeaders extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      payload: [],
      error: '',
    };
  }

  handleChange(event) {
    const { id, valeu } = event.target;
    this.setState({ [id]: valeu });
  }

  render() {
    return (
      <form>
        <label htmlFor="Procurar">Procurar: </label>
        <input id="Procurar" type="text" onChange={this.handleChange} />
      </form>
    )
  }
};

const mapDispatchToProps = (dispatch) => ({
  ImportReduxThunk: e => dispatch(ReduxThunk(e)),
});

export default connect(null, mapDispatchToProps)(TableHeaders);
