import React from 'react';
import { connect } from 'react-redux';
import { filterValues } from '../actions/actionFilterPlanetsName';
class FiltroNumericos extends React.Component {
  arrComTodosOsDados() {
    const { data, column } = this.props;
    let filtrar = data;
    filtrar = filtrar.map((coluna) => coluna.population.includes(column));
  };

  render() {
    const { dispatchFilterValues } = this.props;
    console.log('FiltroNumericos', this.props);
    return (
      <div>
        <select data-testid="column-filter">
          <option disabled selected>Coluna</option>
          <option>population</option>
          <option>orbital_period</option>
          <option>diameter</option>
          <option>rotation_period</option>
          <option>surface_water</option>
        </select>
        <select data-testid="comparison-filter">
          <option disabled selected>Comparação</option>
          <option>Maior que</option>
          <option>Menor que</option>
          <option>Igual</option>
          {this.arrComTodosOsDados()}
        </select>
        <input type="number" data-testid="value-filter" />
        <button data-testid="button-filter"onClick={(e) => dispatchFilterValues(e.target.value)}>Filtrar</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  // console.log('filtros numericos', state);
  return {
    column: state.reducerFilter.column,
    comparison: state.reducerFilter.comparison,
    value: state.reducerFilter.value,
    data: state.planetsReducer.data,
  };
};

const mapDispatchToProps = (dispatch) => ({
  dispatchFilterValues: (column, comparison, value) => dispatch(filterValues(column, comparison, value)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FiltroNumericos);
