import { CHANGE_NAME } from '../action';

const INICIAL_STATE = {
    nome: '',
};

const nameReducer  = (state= INICIAL_STATE, action) => {
    if(action.type === CHANGE_NAME) {
        return {
            ...state,
            nome: action.nome,
        }
    }
    return state
};

export default nameReducer;
